import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { 
  Container, 
  CssBaseline, 
  Typography, 
  Box, 
  Grid, 
  Card, 
  CardContent,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TablePagination
} from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#b71c1c',
    },
    secondary: {
      main: '#ffd700',
    },
  },
});

function parseCSV(csv) {
  const lines = csv.split('\n');
  const headers = lines[0].split(',');

  return lines.slice(1).map(line => {
    const values = line.split(',');
    return headers.reduce((obj, header, index) => {
      obj[header.trim()] = values[index]?.trim() || '';
      return obj;
    }, {});
  });
}

function formatDate(dateStr) {
  if (!dateStr) return 'Unknown';
  try {
    const date = new Date(dateStr);
    const year = Math.abs(date.getFullYear());
    const era = date.getFullYear() < 0 ? 'BCE' : 'CE';
    return `${year} ${era}`;
  } catch {
    return 'Unknown';
  }
}

function App() {
  const [emperors, setEmperors] = useState([]);
  const [dynastyStats, setDynastyStats] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  useEffect(() => {
    fetch('/roman-emperors.csv')
      .then((response) => response.text())
      .then((csv) => {
        const records = parseCSV(csv);
        setEmperors(records);

        // Calculate dynasty statistics
        const dynastyCounts = records.reduce((acc, emperor) => {
          if (emperor.Dynasty) {
            acc[emperor.Dynasty] = (acc[emperor.Dynasty] || 0) + 1;
          }
          return acc;
        }, {});

        const stats = Object.entries(dynastyCounts)
          .map(([name, count]) => ({
            name,
            count,
          }))
          .filter(stat => stat.name);

        setDynastyStats(stats.sort((a, b) => b.count - a.count));
      });
  }, []);

  const calculateStats = () => {
    if (!emperors.length) return { total: 0, avgReign: 0, violent: 0 };

    const violent = emperors.filter(
      (e) => e.Cause === 'Assassination' || e.Cause === 'Execution' || e.Cause === 'Died in Battle'
    ).length;

    return {
      total: emperors.length,
      violent: Math.round((violent / emperors.length) * 100),
    };
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const stats = calculateStats();

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h2" component="h1" gutterBottom align="center" color="secondary">
            Roman Emperors Analysis
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Typography variant="h5" gutterBottom>
                    Key Statistics
                  </Typography>
                  <Typography variant="body1">
                    Total Emperors: {stats.total}
                  </Typography>
                  <Typography variant="body1">
                    Violent Deaths: {stats.violent}%
                  </Typography>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Typography variant="h5" gutterBottom>
                    Emperors by Dynasty
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={dynastyStats}>
                      <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                      <YAxis />
                      <Tooltip />
                      <Bar dataKey="count" fill="#b71c1c" />
                    </BarChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12}>
              <Card>
                <CardContent>
                  <Typography variant="h5" gutterBottom>
                    Emperors List
                  </Typography>
                  <TableContainer component={Paper}>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Name</TableCell>
                          <TableCell>Birth</TableCell>
                          <TableCell>Death</TableCell>
                          <TableCell>Cause of Death</TableCell>
                          <TableCell>Dynasty</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {emperors
                          .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                          .map((emperor, index) => (
                          <TableRow key={index}>
                            <TableCell>{emperor.Name}</TableCell>
                            <TableCell>{formatDate(emperor.Birth)}</TableCell>
                            <TableCell>{formatDate(emperor.Death)}</TableCell>
                            <TableCell>{emperor.Cause || 'Unknown'}</TableCell>
                            <TableCell>{emperor.Dynasty}</TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                    <TablePagination
                      rowsPerPageOptions={[5, 10, 25]}
                      component="div"
                      count={emperors.length}
                      rowsPerPage={rowsPerPage}
                      page={page}
                      onPageChange={handleChangePage}
                      onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                  </TableContainer>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
