import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Container, CssBaseline, Typography, Box, Grid, Card, CardContent } from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
import emperorsData from './roman-emperors.csv';
import { parse } from 'csv-parse/sync';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#b71c1c',
    },
    secondary: {
      main: '#ffd700',
    },
  },
});

function App() {
  const [emperors, setEmperors] = useState([]);
  const [dynastyStats, setDynastyStats] = useState([]);

  useEffect(() => {
    fetch(emperorsData)
      .then((response) => response.text())
      .then((csv) => {
        const records = parse(csv, { columns: true });
        setEmperors(records);

        // Calculate dynasty statistics
        const dynastyCounts = records.reduce((acc, emperor) => {
          acc[emperor.Dynasty] = (acc[emperor.Dynasty] || 0) + 1;
          return acc;
        }, {});

        const stats = Object.entries(dynastyCounts).map(([name, count]) => ({
          name,
          count,
        }));

        setDynastyStats(stats.sort((a, b) => b.count - a.count));
      });
  }, []);

  const calculateStats = () => {
    if (!emperors.length) return { total: 0, avgReign: 0, violent: 0 };

    const violent = emperors.filter(
      (e) => e.Cause === 'Assassination' || e.Cause === 'Execution' || e.Cause === 'Died in Battle'
    ).length;

    return {
      total: emperors.length,
      violent: Math.round((violent / emperors.length) * 100),
    };
  };

  const stats = calculateStats();

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h2" component="h1" gutterBottom align="center" color="secondary">
            Roman Emperors Analysis
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Typography variant="h5" gutterBottom>
                    Key Statistics
                  </Typography>
                  <Typography variant="body1">
                    Total Emperors: {stats.total}
                  </Typography>
                  <Typography variant="body1">
                    Violent Deaths: {stats.violent}%
                  </Typography>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Typography variant="h5" gutterBottom>
                    Emperors by Dynasty
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={dynastyStats}>
                      <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                      <YAxis />
                      <Tooltip />
                      <Bar dataKey="count" fill="#b71c1c" />
                    </BarChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
